import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { userActions } from '../../actions';

class LoginPage extends React.Component {
  constructor(props) {
    super(props);
    
    document.title = "React Test | Login";

    // reset login status
    this.props.logout();

    this.state = {
      username: '',
      password: '',
      submitted: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit(e) {
    e.preventDefault();

    this.setState({ submitted: true });
    const { username, password } = this.state;
    if (username && password) {
      this.props.login(username, password);
    }
  }

  render() {
    const { loggingIn } = this.props;
    const { username, password, submitted } = this.state;
    return (
      <div className="wrapper">
        <div className="login-register">
          <h2>Login</h2>
          <form name="form" onSubmit={this.handleSubmit}>
            <div>
              <input type="text" placeholder="Username" name="username" value={username} onChange={this.handleChange} required/>
              {submitted && !username &&
                <div className="helper">Username is required</div>
              }
            </div>
            <div>
              <input type="password" placeholder="Password" name="password" value={password} onChange={this.handleChange} required/>
              {submitted && !password &&
                <div className="helper">Password is required</div>
              }
            </div>
            <div className="form-control">
              <button title="Login">Login</button>
              {loggingIn &&
                <img className="loading" src="data:image/gif;base64," />
              }
              <Link to="/register" title="Register">Register</Link>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

function mapState(state) {
  const { loggingIn } = state.authentication;
  return { loggingIn };
}

const actionCreators = {
  login: userActions.login,
  logout: userActions.logout
};

const connectedLoginPage = connect(mapState, actionCreators)(LoginPage);
export { connectedLoginPage as LoginPage };